import React from 'react';
import './contactMeInput.css';

const contactMeInput = (props) => {

    let inputElement = null;

    switch (props.elementType) { 
        case ('input'):
            inputElement = <input {...props.elementConfig} className={'InputElement'} value={props.value} />;
            break;
        case ('textarea'):
            inputElement = <textarea {...props.elementConfig} className={'InputElement contactMeTextarea'} cols={2} value={props.value} />;
            break;
        default: 
            inputElement = <input {...props.elementConfig} className={'InputElement'} value={props.value} />;
            break;
    }
    return ( 
        <div className={'contactMeInput'}>
            <label className={'Label'}>{props.label}</label>
            {inputElement}
        </div>
     );
}

export default contactMeInput;
